import pandas as pdimport timeimport jsonimport randomimport requestsfrom requests.auth import HTTPBasicAuthimport urllib3urllib3.disable_warnings(urllib3.exceptions.InsecureRequestWarning)USERNAME = 'IoTGrupa23'PASSWORD = 'IoTProject123'def send_data(datatype, data):    url = 'https://161.53.19.19:56443/m2m/data'    headers = {        'Content-Type': 'application/vnd.ericsson.simple.input.hierarchical+json;version=1.0'    }    data = {        "deviceId": "Grupatual",        "header": {            "timestamp": int(time.time() * 1000)        },        "body": {            "Gtual": {                datatype: round(data,2)            }        }    }    try:        response = requests.post(url, headers=headers, data=json.dumps(data), auth=HTTPBasicAuth(USERNAME, PASSWORD), verify=False)        print(f'Sent {datatype} data: {data}, Response: {response.status_code}, {response.text}')    except Exception as e:        print(f'Failed to send {datatype} data: {data}, Error: {str(e)}')def load_data():    data = pd.read_csv('waterdata.csv')    return datadef send_random_data(data):    while True:        random_row = data.sample()        ph = random_row['ph'].values[0]        solids = random_row['Solids'].values[0]                if random.random() < 0.1:            choice = random.choice(['ph', 'solids'])            if choice == 'ph':                ph = 12            else:                solids = 55000        send_data('G23ph', ph)        send_data('G23Solids', solids)        time.sleep(20)data = load_data()send_random_data(data)